package utils

import (
	"fmt"
	"strings"

	"github.com/solo-io/solo-kit/pkg/api/v1/resources/core"
)

const (
	TranslatorKey = "created_by"

	// label value applied to Proxies generated by the Gloo Edge translator
	GlooEdgeTranslatorValue = "gloo-gateway-translator"
	// label value applied to Proxies generated by the Gloo Gateway translator
	GlooGatewayTranslatorValue = "gloo-kube-gateway-api-translator"
)

func GetTranslatorSelectorExpression(translators ...string) string {
	return fmt.Sprintf("%s in (%s)", TranslatorKey, strings.Join(translators, ", "))
}

func GetTranslatorValue(meta *core.Metadata) string {
	return meta.GetLabels()[TranslatorKey]
}
